					// a macro in order to replace it.
					if (!options.placeholder) {
						// but we can't...
						entry.impossible = true;
					} else {
						var value = options.placeholder.getPlaceholderFor(entry.output,handler.name)
						quotedValue = "<<"+value+">>";
					}
