</tr>

<<.make-table Custom "" yes>>

<$list
	filter="[plugin-type[plugin]![$:/core]![$:/plugins/flibbles/relink]]">
<$set name="subtitle" value={{!!description}} emptyValue={{!!title}} >
<$macrocall $name=".make-table" title=<<subtitle>> plugin=<<currentTiddler>> />
